<?phpif (isset($_GET['p'])) {	$parcours = $_GET['p'];}?><html>	<head>		<title>Parcours <?php echo $parcours;?></title>		<meta http-equiv="Content-Type" content="text/html; charset=UTF8" />		<!-- Elément Google Maps indiquant que la carte doit être affiché en plein écran et		 qu'elle ne peut pas être redimensionnée par l'utilisateur -->		<meta name="viewport" content="initial-scale=1.0, user-scalable=no" />		<!-- Inclusion de l'API Google MAPS -->		<!-- Le paramètre "sensor" indique si cette application utilise détecteur pour déterminer la position de l'utilisateur -->		<!--<script type="text/javascript" src="http://maps.google.com/maps/api/js?sensor=false"></script>-->		<script type="text/javascript">			function initMap() {				map = new google.maps.Map(document.getElementById('map'), {					center: new google.maps.LatLng(45.23, 5.8),					zoom: 12,					mapTypeId: 'satellite'				});				loadKmlLayer('http://www.cross-biviers.fr/maps/<?php echo $parcours;?>.kml', map);			}			function loadKmlLayer(src, map) {				var kmlLayer = new google.maps.KmlLayer(src, {					suppressInfoWindows: true,					preserveViewport: false,					map: map				});				google.maps.event.addListener(kmlLayer, 'click', function(event) {					var content = event.featureData.infoWindowHtml;					var testimonial = document.getElementById('capture');					testimonial.innerHTML = content;				});			} 		</script >		<script async defer			src="https://maps.googleapis.com/maps/api/js?key=AIzaSyAf-fNzB3sEkdo_9O5eMvqPi688qgFo34Y&callback=initMap">		</script>	</head>	<body>		<div id="map" style="border: 1px solid black;							width: 100%; height: 800px;							margin:0px;"></div>	</body></html>